const rawText = $input.first().json.response.text;
const parsedData = JSON.parse(rawText);
const scenes = parsedData.scenes;

// Convert key-value scene objects
const storyScenes = scenes.map((scene, index) => {
  const sceneDetails = `scene_${index + 1}`;
  return {
    json: {
      scene: scene[sceneDetails]
    },
  };
});

return storyScenes;
