const rawText = $('Wait For Scenes').first().json.text;
const parsedData = JSON.parse(rawText);
const storyScene = parsedData.scenes;

// Convert key-value scene objects
const storyScenes = storyScene?.map((scene, index) => {
  const sceneDetails = `scene_${index + 1}`;
  return {
    json: {
      scene: scene[sceneDetails]
    }
  };
});

return storyScenes;

function getImageLinks(endpoint, count = 8) {
  const imageLinks = [];
  for (let i = 1; i <= count; i++) {
    imageLinks.push(`${endpoint}/images/image-${i}.webp`);
  }
  return imageLinks;
}

const bucketEndpoint = $('Preset Values').first().json['R2 Endpoint'];
const imageLinks = getImageLinks(bucketEndpoint);

return [
  {
    json: {
      images: imageLinks
    }
  }
];

const elevenLabs = $('Preset Values').first().json['ElevenLabs API'];
const currentDay = new Date().getDay();

return [
  {
    json: {
      elevenlabs: elevenLabs[currentDay % elevenLabs.length]
    }
  }
];

const rawData = $('Wait For Prompts').all();

return rawData.map(item => {
  const cleanedPrompt = item.json.text
    .replace(/\s+/g, ' ')
    .replace(/"/g, '\\"')
    .trim();

  return {
    json: {
      prompt: cleanedPrompt,
      seed: 1311721057,
      randomize_seed: false,
      width: 720,
      height: 1280,
      num_inference_steps: 25
    }
  };
});

