const rawPrompt = $('Wait For Thumb Prompt').first().json.text;
const cleanedPrompt = rawPrompt
  .replace(/\s+/g, ' ')
  .replace(/"/g, '\\"')
  .trim();

return [
  {
    json: {
      prompt: cleanedPrompt,
      seed: 1311721057,
      randomize_seed: false,
      width: 720,
      height: 1280,
      num_inference_steps: 25
    }
  }
];

const payloadData = $('Merge All Items').first().json;
const combinedImages = [...payloadData["Video Images"], payloadData["Video Thumb"]];

return [
  {
    json: {
      images: combinedImages,
      voiceover: payloadData["Voiceover"],
      background: payloadData["Background Audio"]
    }
  }
];

